package drawio

import (
	_ "embed"
	"os"
	"text/template"
)

//go:embed connectivityMap.drawio.tmpl
var drawioTemplate string

type drawioData struct {
	IconSize   int
	FipXOffset int
	FipYOffset int
	VSIXOffset int
	VSIYOffset int
	VSISize    int
	ShowNIIcon bool
	Nodes      []TreeNodeInterface
}

func (data *drawioData) FIPStyle() string {
	return "shape=image;aspect=fixed;image=data:image/svg+xml,;fontFamily=IBM Plex Sans;fontSource=fonts%2FIBMPlexSans-Regular.woff;fontSize=14;labelPosition=center;verticalLabelPosition=bottom;align=center;verticalAlign=top;spacingTop=-7;"
}
func (data *drawioData) VsiStyle() string {
	return "shape=image;aspect=fixed;image=data:image/svg+xml,;fontFamily=IBM Plex Sans;fontSource=fonts%2FIBMPlexSans-Regular.woff;fontSize=14;spacingTop=-7;labelPosition=center;verticalLabelPosition=bottom;align=center;verticalAlign=top;"
}
func (data *drawioData) Style(tn TreeNodeInterface) string {
	switch {
	case tn.IsVSI():
		return data.VsiStyle()
	case tn.IsNI():
		return "ellipse;whiteSpace=wrap;html=1;aspect=fixed;labelBorderColor=none;horizontal=1;resizable=0;rotatable=0;expand=0;fillColor=#FF8000;strokeColor=none;fontColor=#FFFFFF;fontSize=27;fontFamily=Times New Roman;"
	case tn.IsUser():
		return "shape=image;aspect=fixed;image=data:image/svg+xml,;fontFamily=IBM Plex Sans;fontSource=fonts%2FIBMPlexSans-Regular.woff;fontSize=14;labelPosition=center;verticalLabelPosition=bottom;align=center;verticalAlign=top;spacingTop=-7;"
	case tn.IsGateway():
		return "shape=image;aspect=fixed;image=data:image/svg+xml,;fontSize=14;fontFamily=IBM Plex Sans;fontSource=fonts%2FIBMPlexSans-Regular.woff;spacingTop=-7;labelPosition=center;verticalLabelPosition=bottom;align=center;verticalAlign=top;"
	case tn.IsInternet():
		return "shape=image;aspect=fixed;image=data:image/svg+xml,;fontFamily=IBM Plex Sans;fontSource=fonts%2FIBMPlexSans-Regular.woff;fontSize=14;labelPosition=center;verticalLabelPosition=bottom;align=center;verticalAlign=top;spacingTop=-7;"
	case tn.IsInternetService():
		return "shape=image;aspect=fixed;image=data:image/svg+xml,;fontSize=14;labelPosition=center;verticalLabelPosition=bottom;align=center;verticalAlign=top;fontFamily=IBM Plex Sans;fontSource=fonts%2FIBMPlexSans-Regular.woff;spacingTop=-6;"
	case tn.IsEndpoint():
		return "shape=image;aspect=fixed;image=data:image/svg+xml,;fontSize=14;fontFamily=IBM Plex Sans;fontSource=fonts%2FIBMPlexSans-Regular.woff;spacingTop=-7;labelPosition=center;verticalLabelPosition=bottom;align=center;verticalAlign=top;"
	}
	return ""
}

func CreateDrawioConnectivityMapFile(network SquareTreeNodeInterface, outputFile string) {
	newLayout(network).layout()
	data := &drawioData{
		iconSize,
		fipXOffset,
		fipYOffset,
		vsiXOffset,
		vsiYOffset,
		vsiIconSize,
		hasVsiIcon(network),
		getAllNodes(network)}
	writeDrawioFile(data, outputFile)
}

func writeDrawioFile(data *drawioData, outputFile string) {
	tmpl, err := template.New("connectivityMap.drawio.tmpl").Parse(drawioTemplate)
	if err != nil {
		panic(err)
	}
	fo, err := os.Create(outputFile)
	if err != nil {
		panic(err)
	}
	// close fo on exit and check for its returned error
	defer func() {
		if err = fo.Close(); err != nil {
			panic(err)
		}
	}()
	err = tmpl.Execute(fo, data)
	if err != nil {
		panic(err)
	}
}
